import React, { useState, useEffect, useRef, useReducer, createContext, useContext } from 'react';


const TaskContext = createContext();

const taskReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TASK':
      return [...state, action.payload];
    case 'TOGGLE_TASK':
      return state.map(task => task.id === action.payload ? { ...task, completed: !task.completed } : task);
    case 'SET_TASKS':
      return action.payload;
    default:
      return state;
  }
};

const Calculator = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState(null);

  const handleClick = (value) => {
    if (value === '=') {
      try {
        setResult(eval(input));
      } catch {
        setResult('Error');
      }
    } else if (value === 'C') {
      setInput('');
      setResult(null);
    } else {
      setInput(prev => prev + value);
    }
  };

  return (
    <div>
      <h2>Calculator</h2>
      <div>{input}</div>
      <div>{result !== null && `= ${result}`}</div>
      <div>
        {[1, 2, 3, 4, 5, 6, 7, 8, 9, 0, '+', '-', '*', '/', '=', 'C'].map((btn) => (
          <button key={btn} onClick={() => handleClick(btn.toString())}>{btn}</button>
        ))}
      </div>
    </div>
  );
};

const TaskList = () => {
  const { tasks, dispatch } = useContext(TaskContext);

  return (
    <div>
      <h2>Task List</h2>
      <ul>
        {tasks.map(task => (
          <li key={task.id} style={{ color: task.completed ? 'green' : 'black' }}>
            {task.text} <button onClick={() => dispatch({ type: 'TOGGLE_TASK', payload: task.id })}>Toggle</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

const TaskManager = () => {
  const [state, dispatch] = useReducer(taskReducer, []);
  const inputRef = useRef();

  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/todos?_limit=5')
      .then(response => dispatch({ type: 'SET_TASKS', payload: response.data.map(task => ({ id: task.id, text: task.title, completed: task.completed })) }));
  }, []);

  const addTask = () => {
    const text = inputRef.current.value;
    if (text) {
      dispatch({ type: 'ADD_TASK', payload: { id: Date.now(), text, completed: false } });
      inputRef.current.value = '';
    }
  };

  return (
    <TaskContext.Provider value={{ tasks: state, dispatch }}>
      <div>
        <h2>Task Manager</h2>
        <input ref={inputRef} type="text" placeholder="Add a task" />
        <button onClick={addTask}>Add</button>
        <TaskList />
      </div>
    </TaskContext.Provider>
  );
};

const App = () => (
  <div>
    <Calculator />
    <TaskManager />
  </div>
);

export default App;
